<style lang="scss">
$color:#FFFF00;
  .navigation{
    padding:0 15px;
    background-color:#EFEFEF;
    position:relative;
    &>div{
      svg{
        vertical-align:middle;
        margin-top:-4px;
      }
      // display:flex;
      // align-itmes:center;
        button{
        height: 100%;
        border:none;
        background-color:transparent;
        cursor: pointer;
        font-weight:700;
        font-size: 16px;
        &:focus{
          outline:none;
        }
      }
    }
    &-headlines{
      padding:20px 0px 50px;
      &.login{
        padding:20px 0px 10px;
      }
      &>div{
        // flex:1;
        &:first-child{
          text-align:left;
          font-weight: 900;
        }
        &:last-child{
          text-align:right;
        }
      }
    }
    &-fiv{
      // padding:0 30px;
      // min-height:44px;
      line-height:35px;
      &>div{
        min-height:inherit;
        &.view{
          font-size:16px;
          font-weight:700;
          button{
            padding:0;
            i{
              margin: 0 5px;
                opacity:0.5;
              &.open{
                opacity:1;
              }
            }
          }
        }
        &.phone{
            @media(max-width: 767px){
                  height:50px;
                  width:140px;
                  margin: 0 auto;
                  padding:0;
                  }
          ul{
            margin: 0;
            min-height:inherit;
            position: relative;
            .navi-title-bottom{
              position:absolute;
              bottom:0;
              width:100%;
              height:2px;
              background-color:#000;
              transition: transform 0.2s, width 0.3s;
            }
            @media(max-width: 767px){
                  box-shadow: inset 0 -2px 0 #41307c;
                  background-color:#fff;
                  position:absolute;
                  left: 0%;
                  top: 0;
                  width:100%;
                  text-align:center;
                  border-bottom:2px solid #222;
                  height:50px;
                  overflow:hidden;
                  }
               &.open{
                 box-shadow:inset 0 -2px 0 #41307c, 0 2px 10px rgba(0, 0, 0, 0.2);
               }
            li{
              min-height:inherit;
              display: inline-block;
              // button {
              //   border:none;
              //   background-color: transparent;
              // }
                @media(max-width: 767px){
                  display: block;
                  height:50px;
                  }
              }
          }
        }
        &:first-child{
          text-align:center;
        }
        &:last-child{
          text-align:right;
        }

      }
    }
    .filter{
      position:absolute;
      left:15px;
      bottom:3px;
      z-index:9;
    }
    .side-columns{
      position:absolute;
      left: 0;
      height: calc(100vh - 135px);
      top:calc(100% - 44px);
      z-index:8;
      width:25%;
      min-width:200px;
      transform:translate3d(-100%, 0, 0);
      transition:transform 0.5s;
      opacity: 0.99;
      &.open{
           transform:translate3d(0, 0, 0);
           box-shadow: 0px -2px 14px 0px rgba(0,0,0,0.09);
      }
        &-header{
          background-color:$color;
          height:44px;
          button{
            padding:0 15px;
            float: right;
            background-color:$color;
          }
        }
        &-list{
          background-color:rgba(255,255,255,.98);
          padding:15px;
          i{
            margin-right:5px;
          }
          h4{
            padding:10px 0;
          }
          &-search{
            padding-bottom:20px;
            position:relative;
            input{
              height:40px;
              border:none;
              border-radius: 50px;
              background-color:#eee;
              padding-left:40px;
              width:100%;
            }
          }

          &-Category{
            &-tabulation{
              background-color:#eee;
              padding:15px 10px;
              border-radius:5px;
              height:300px;
              overflow-y:auto;
              p{
                padding:5px 0;
                margin:0;
                cursor:pointer;
              }
            }
          }

          &-Tags{
            span{
              background-color:#eee;
              float:left;
              margin:3px 5px 3px 0px;
              padding:5px 10px;
              cursor:pointer;
              &.active{
                background-color:#BBBBBB;
              }
            }
          }
        }
    }
  }
  .sprite_login{
    width:30px;
    height:30px;
    display:inline-block;
    background:url('../../static/svg_sprite.svg') no-repeat;
    background-position:-45px -55px;
    vertical-align: -10px;
  }
  .sprite_view-1{
    @extend .sprite_login;
    width:20px;
    height:20px;
    background-position: -35px -95px;
    vertical-align: -3px;
  }
  .sprite_view-2{
    @extend .sprite_view-1;
    background-position: -65px -95px;
  }
  .sprite_filter{
    @extend .sprite_login;
    background-position: 0px -90px;
  }
  .sprite_side{
    @extend .sprite_login;
    background-position: -330px -90px;
  }
  .sprite_find{
    @extend .sprite_login;
    background-position: -150px -90px;
    position:absolute;
    top:5px;  
    left:8px;
  }
  .sprite_category{
    @extend .sprite_view-1;
    background-position:-185px -95px;
  }
  .sprite_tags{
    @extend .sprite_view-1;
    background-position:-215px -95px;
  }
  .sprite_color{
    @extend .sprite_view-1;
    background-position:-245px -95px;
  }
  .login-information{
    text-align:center;
    h4{
      font-size:24px;
    }
    p{
      font-size:14px;
      color:#aaa;
      padding:20px 0 25px;
    }
  }
</style>

