<style lang="scss" scoped>
$color:#FFFF00;
  .navigation{
    padding:0 15px;
    background-color:#EFEFEF;
    position:relative;
    &>div{
      svg{
        vertical-align:middle;
      }
      // display:flex;
      // align-itmes:center;
        button{
          height: 100%;
        border:none;
        background-color:transparent;
        cursor: pointer;
        &:focus{
          outline:none;
        }
      }
    }
    &-headlines{
      padding:50px 0px;
      &>div{
        // flex:1;
        &:first-child{
          text-align:left;
          font-weight: 900;
        }
        &:last-child{
          text-align:right;
        }
      }
    }
    &-fiv{
      // padding:0 30px;
      min-height:44px;
      line-height:44px;
      &>div{
        min-height:inherit;
        &.phone{
            @media(max-width: 767px){
                  height:50px;
                  width:140px;
                  margin: 0 auto;
                  padding:0;
                  }
          ul{
            margin: 0;
            min-height:inherit;
            @media(max-width: 767px){
                  box-shadow: inset 0 -2px 0 #41307c;
                  background-color:#fff;
                  position:absolute;
                  left: 0%;
                  top: 0;
                  width:100%;
                  text-align:center;
                  border-bottom:2px solid #222;
                  height:50px;
                  overflow:hidden;
                  }
               &.open{
                 box-shadow:inset 0 -2px 0 #41307c, 0 2px 10px rgba(0, 0, 0, 0.2);
               }
            li{
              min-height:inherit;
              display: inline-block;
                @media(max-width: 767px){
                  display: block;
                  height:50px;
                  }
              }
          }
        }
        &:first-child{
          text-align:center;
        }
        &:last-child{
          text-align:right;
        }

      }
    }
    .filter{
      position:absolute;
      left:15px;
      bottom:10px;
      z-index:9;
    }
    .side-columns{
      position:absolute;
      left: 0;
      height: calc(100vh - 135px);
      top:calc(100% - 44px);
      z-index:8;
      width:25%;
      min-width:200px;
      transform:translate3d(-100%, 0, 0);
      transition:transform 0.5s;
      /* Rectangle 7: */
      opacity: 0.99;
      &.open{
           transform:translate3d(0, 0, 0);
           box-shadow: 0px -2px 14px 0px rgba(0,0,0,0.09);
      }
        &-header{
          background-color:$color;
          height:44px;
          button{
            padding:0 15px;
            float: right;
            background-color:$color;
          }
        }
        &-list{
          background-color:rgba(255,255,255,.98);
          padding:15px;
          &-search{
            padding-bottom:20px;
            input{
              height:40px;
              border:none;
              border-radius: 50px;
              background-color:#eee;
              padding-left:40px;
              width:100%;
            }
          }

          &-Category{
            &-tabulation{
              background-color:#eee;
              padding:15px 10px;
              border-radius:5px;
              height:300px;
              overflow-y:auto;
              p{
                padding:5px 0;
                margin:0;
                cursor:pointer;
              }
            }
          }

          &-Tags{
            span{
              background-color:#eee;
              float:left;
              margin:3px 5px 3px 0px;
              padding:5px 10px;
              cursor:pointer;
              &.active{
                background-color:#BBBBBB;
              }
            }
          }
        }
    }
  }
</style>
